import styled from "styled-components";

export const Wrapper = styled.div`
  border: 1px solid #ccc;
  border-radius: 8px;
  padding: 16px;
  margin: 8px;
  width: 250px;
  background-color: #fff;
  display: flex;
  flex-direction: column;
  justify-content: space-between;

  h2 {
    font-size: 18px;
    margin: 0 0 8px 0;

    a {
      text-decoration: none;
      color: #0366d6;

      &:hover {
        text-decoration: underline;
      }
    }
  }

  p {
    font-size: 14px;
    margin: 4px 0;
    color: #333;
  }
`;

export const WrapperStats = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 12px;
  font-size: 14px;
  color: #555;

  span {
    display: flex;
    align-items: center;
    gap: 4px;
  }
`;

export const WrapperLanguage = styled.div`
  margin-top: 8px;
  font-size: 13px;
  color: #666;
  font-style: italic;
`;
